import styled from "styled-components";
import styles from "./Body.module.css";
import { useContext, useEffect, useState } from "react";
import { PrContent } from "../contexts/prContent";
import mouse from "../assets/마우스1.png"
function Body2({ content, content2 }) {
  const ImgWrapper = styled.div`
    width: 100vw;
    height: 100vh;
    object-fit: cover;
    color: white;
  `;

const ImgWraper2 = styled.div`
        width: 15px;
        height: 20px;
        object-fit: cover;
        margin: 16px 0 8px 0;
        color: white;
    `
  // const [cardFlipped, setCardFlipped] = useState(false);
  // const [card1Clicked, setCard1Clicked] = useState(false);

  // const onClick = () => {
  //   if (!cardFlipped) {
  //     setCard1Clicked(true); 
  //     setCardFlipped(true); 
  //   } else {
  //     setCardFlipped(false); 
  //   }
  // };
  const temp = useContext(PrContent)
const {cardFlipped, onClick} = temp;
  return (
    <>
    {/* 맨처음 화면에서 이동했을때 한번만 보여지는 화면 */}
      {content && (
        <div className={styles.card_container}>
          <div className={styles.wrap} >
            <div className={`${cardFlipped ? styles.card1 : ""}`}>
              
            </div>
            <div className={`${cardFlipped ? styles.card2 : ""}`}>
              
            </div>
            <div onClick={onClick} className={`${styles.card} ${cardFlipped ? styles.flipped : ""}`}>
              <div className={styles.card_front}>
                <div className={styles.front_wrap}>
                  <h2>이름 : 전준희</h2>
                  <p>나이 : 만(24세)</p>
                  <p>학력 : 배재대학교 컴퓨터공학과 졸업</p>
                  <p>기술 스택 : HTML, CSS, JavaScript, React</p>
                </div>
                <ImgWraper2>
                  <img  className={styles.mouse} src={mouse} />
                </ImgWraper2>
              </div>
              <div className={styles.card_back}>BACK</div>
            </div>
          </div>
        </div>
      )}
      {/* nav바로 JN을 클릭했을때 계속해서 보여지는 화면 */}
      {content2 && (
        <>
          <ImgWrapper>
            <p>안녕하세요</p>
          </ImgWrapper>
          <div className={styles.pos1}>
            <h1>신입 프론트개발자를 희망하는 전준희 입니다. 잘 부탁드립니다.</h1>
          </div>
        </>
      )}
    </>
  );
}

export default Body2;
