import styled from "styled-components";
import styles from "./Body.module.css";
import { useContext, useEffect } from "react";
import { PrContent } from "../contexts/prContent";

function Body2({ content, content2 }) {
  const ImgWrapper = styled.div`
    width: 100vw;
    height: 100vh;
    object-fit: cover;
    color: white;
  `;

const Snowflake = () => {
  return (
    <div className="snowflake">
      ❅ {/* 눈송이 아이콘 */}
    </div>
  );
};
const numFlakes = 50;
  
// 눈송이 컴포넌트를 numFlakes만큼 반복하여 생성
const snowflakes = [];
for (let i = 0; i < numFlakes; i++) {
  snowflakes.push(<Snowflake key={i} />);
}

  return (
    <>
      {content2 && (
        <div className="snow-container">{snowflakes}</div>
      )}
      {content && (
        <>
          <ImgWrapper>
            <p>안녕하세요</p>
          </ImgWrapper>
          <div className={styles.pos1}>
            <h1>신입 프론트개발자를 희망하는 전준희 입니다. 잘 부탁드립니다.</h1>
          </div>
        </>
      )}

      
    </>
  );
}

export default Body2;
