import { useEffect } from "react";
import styles from "./Portfoliohover.module.css";

function Portfoliohover({ handleCloseModal }) {
    useEffect(() => {
        const handleKeyPress = (e) => {
            if (e.keyCode === 27) { // ESC 키 코드
                handleCloseModal();
            }
        };

        document.addEventListener("keydown", handleKeyPress);

        return () => {
            document.removeEventListener("keydown", handleKeyPress);
        };
    }, [handleCloseModal]);

    const handleClickOutside = (e) => {
        if (!e.target.closest(`.${styles.container}`)) {
            handleCloseModal();
        }
    };

    return (
        <div className={styles.overlay} onClick={handleClickOutside}>
            <div className={styles.modal}>
                <p onClick={handleCloseModal}>삭제</p>
            </div>
        </div>
    );
}

export default Portfoliohover;
