import styled from "styled-components";
import styles from "./Body.module.css";
import { useContext, useEffect } from "react";
import { PrContent } from "../contexts/prContent";

function Body2({ content, content2 }) {
  const ImgWrapper = styled.div`
    width: 100vw;
    height: 100vh;
    object-fit: cover;
    color: white;
  `;
// const random = () =>{
//   return Math.floor(Math.random() * window.innerWidth)
// }
// const creataeSnow = () =>{
//   const el = document.createElement("div")
//   el.classList.add("snow")
//   el.style.marginLeft = random() + 'px'
//   document.body.appendChild(el)
// }
// useEffect(()=>{
//   for(let i = 0; i < 300; i++){
//     creataeSnow();
//   }
// },[])
useEffect(() => {
  const createSnow = () => {
    const snowflake = document.createElement("div");
    snowflake.classList.add(styles.snowflake);
    snowflake.style.left = `${Math.random() * window.innerWidth}px`;
    snowflake.style.animationDuration = `${Math.random() * 3 + 2}s`; // 눈송이가 떨어지는 시간을 랜덤으로 설정
    document.body.appendChild(snowflake);

    setTimeout(() => {
      snowflake.remove(); // 눈송이가 화면 아래로 사라지면 제거
    }, 5000); // 5초 후에 눈송이 제거
  };

  if (content2) {
    const snowInterval = setInterval(createSnow, 100); // 0.1초마다 눈송이 생성
    return () => clearInterval(snowInterval); // 컴포넌트가 unmount되면 interval 제거
  }
}, [content2]);
  return (
    <>
      {content2 && (
        <div className={styles.snow}>
          
        </div>
      )}
      {content && (
        <>
          <ImgWrapper>
            <p>안녕하세요</p>
          </ImgWrapper>
          <div className={styles.pos1}>
            <h1>신입 프론트개발자를 희망하는 전준희 입니다. 잘 부탁드립니다.</h1>
          </div>
        </>
      )}

      
    </>
  );
}

export default Body2;
