import styels from "./Features.module.css"
import img1 from "../../assets/학원.jpg"
import img2 from "../../assets/학원2.jpg"
import img3 from "../../assets/학원3.jpg"
import { useState } from "react"
import Img1 from "./Img1"
import Img2 from "./Img2"
import Img3 from "./Img3"
function Features(){
    const [hover1, setHover1] = useState(true)
    const [hover2, setHover2] = useState(false)
    const [hover3, setHover3] = useState(false)

    const handlehover1 = () =>{
        setHover1(true)
    }
    const handlehover2 = () =>{
        setHover2(true)
    }
    const handlehover3 = () =>{
        setHover3(true)
    }
    // const handlehoverout1 = () =>{
    //     setHover1(false)
    // }
    const handlehover2out = () =>{
        setHover2(false)
    }
    const handlehover3out = () =>{
        setHover3(false)
    }
    return(
        <div className={styels.container}>
            <div className={styels.left}>
                <div className={styels.name}>DW아카데미 학원</div>
                <div className={styels.left_wrap}>
                    <div className={styels.left_wrap_left}>
                        <div onMouseEnter={handlehover1} onMouseLeave={handlehoverout1} className={styels.img_wrap}>
                            <img src={img1} alt="수료증1"/>
                        </div>
                        <div onMouseEnter={handlehover2} onMouseLeave={handlehover2out} className={styels.img_wrap}> 
                            <img src={img2} alt="수료증1"/>
                        </div>
                        <div onMouseEnter={handlehover3} onMouseLeave={handlehover3out} className={styels.img_wrap}>
                            <img src={img3} alt="수료증1"/>
                        </div>
                    </div>
                    <div className={styels.left_wrap_right}>
                        <div className={styels.hover }>
                            {hover1 ? <Img1 /> : hover2 ? <Img2 />: hover3 && <Img3 /> }
                            {/* {hover ? <Img2 /> : "이미지에 마우스를 올려보세요!!"}
                            {hover ? <Img3 /> : "이미지에 마우스를 올려보세요!!"} */}
                        </div>
                    </div>
                </div>
            </div>
            
            <div className={styels.right}>
                <div>배운 기술</div>
            </div>

        </div>
    )
}


export default Features;
