import styled from "styled-components";
import styles from "./Body.module.css";
import { useContext, useEffect } from "react";
import { PrContent } from "../contexts/prContent";

function Body2({ content, content2 }) {
  const ImgWrapper = styled.div`
    width: 100vw;
    height: 100vh;
    object-fit: cover;
    color: white;
  `;

const Snowflake = styled.div`
position: absolute;
width: 10px;
height: 10px;
background-color: white;
border-radius: 50%;
`;

// 눈송이 위치 설정 함수
const setSnowPosition = (snowflake) => {
const x = Math.random() * window.innerWidth;
const y = Math.random() * window.innerHeight;
snowflake.style.left = `${x}px`;
snowflake.style.top = `${y}px`;
};

useEffect(() => {
if (content2) {
  // 100개의 눈송이 생성
  for (let i = 0; i < 100; i++) {
    const snowflake = document.createElement("div");
    snowflake.classList.add(styles.snowflake); // Body.module.css에서 정의한 스타일 클래스 추가
    setSnowPosition(snowflake); // 눈송이 위치 설정
    document.body.appendChild(snowflake); // 문서에 눈송이 추가
  }
}
}, [content2]);
  return (
    <>
      {content2 && (
        <>
         {[...Array(100)].map((_, index) => (
            <Snowflake key={index} />
          ))}
        {/* <div className={styles.snow}></div> */}
        {/* <div className={styles.snow}></div>
        <div className={styles.snow}></div>
        <div className={styles.snow}></div>
        <div className={styles.snow}></div>
        <div className={styles.snow}></div>
        <div className={styles.snow}></div> */}
        </>
      )}
      {content && (
        <>
          <ImgWrapper>
            <p>안녕하세요</p>
          </ImgWrapper>
          <div className={styles.pos1}>
            <h1>신입 프론트개발자를 희망하는 전준희 입니다. 잘 부탁드립니다.</h1>
          </div>
        </>
      )}

      
    </>
  );
}

export default Body2;
