import './App.css';
import Nav from './Nav';
import { useState } from 'react';

function App() {
  const [nav1, setNav] = useState(false);
  const handleClick = (v) => {
    setNav1(v); // 클릭 시 setNav를 통해 nav 상태 변경
  };
  return (
   <>
    <Nav />
    <Outlet setNav={handleClick}/>
    {/* <Footer /> */}
   </>
  );
}

export default App;
