import React from "react";
import { useState, createContext } from "react";

// context 객체 생성
const PrContent = createContext();

export function PrContentProvider({ defaultValue = "ko", children }) {
//   const [locale, setLocale] = useState(defaultValue);
//   const [userInfo, setUserInfo] = useState({});
//   const [thema, setThema] = useState("");
//   const [headIconLeder, setHeadIconLeder] = useState(false);
//   const [chatOnOff, setChatOnOff] = useState(true);
//   const [userStop, setUserStop] = useState("");
//   const [count, setCount] = useState(0);
    const [nav, setNav] = useState(false)
    // const [content, setContent] = useState(false)

    // const handleClick = () =>{
    //   setNav(true)
    //   setTimeout(()=>{
    //     setContent(true)
    //   },1200)
    // }
    const [content2 , setContent2] = useState(false)
    const handleClick2 = () =>{
      setTimeout(()=>{
        setContent2(true)
      },1200)
    }


    const [cardFlipped, setCardFlipped] = useState(false);
    const [card1Clicked, setCard1Clicked] = useState(false);
  
    const onClick = () => {
      if (!cardFlipped) {
        setCard1Clicked(true); 
        setCardFlipped(true); 
      } else {
        setCardFlipped(false); 
      }
    };
  return (
    <PrContent.Provider
      value={{
        nav,
        setNav,
        handleClick2,
        content2,
        setContent2,
        cardFlipped,
        setCardFlipped,
        card1Clicked,
        setCard1Clicked,
        onClick
      }}
    >
      {children}
    </PrContent.Provider>
  );
}

export { PrContent };

