import { useState } from 'react';
import styled, { css } from 'styled-components';
import Head from './Header';

// 공통 스타일 정의




// 공통 부분 --------------------------------------------
export const Container = styled.div`
    width: 100%;
    height: 100%;
`;

export const H1 = styled.h1`
    font-weight: bold;
    font-size: 40px;
    @media (max-width: 1735px) {
        font-size: 20px;
    }   
`;

export const H2 = styled.h2`
`;

export const H3 = styled.h3`
    padding-left: 12px;
    font-size: 30px;
    font-weight: bold;
    position: absolute;
    z-index: 999;
    transition: all 0.5s linear;
    color: ${(props) => (props.hover ? "white" : "black")};
    @media (max-width: 1735px) {
        font-size: 15px;
    }   
`;

export const H4 = styled.h4`
    font-size: 24px;
    font-weight: 300;
`;

export const P = styled.p`
    font-size: 28px;
    @media (max-width: 1735px) {
        font-size: 14px;
    }   
`;

export const Smallp = styled.p`
    font-size: 24px;
    color: white;
    @media (max-width: 1735px) {
        font-size: 12px;
    }   
`;


// ----------------------------------------
const Aboutme = styled.div`
    width: 100%;
    height: 100vh;
    background-color: dodgerblue;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
`


const TitleTob = styled.div`
    width: 100%;
    max-width: 1614px;
    height: 112px;
    background-color: yellow;
    margin: 0 auto;
    display: flex;
    align-items: center;
`
const TitleBottom = styled.div`
    width: 100%;
    max-width: 1614px;
    height: 112px;
    background-color: yellow;
    margin: 0 auto;
    display: flex;
    justify-content: end;
    align-items: center;
    padding-right: 12px;
`
const Aboutmecontent = styled.div`
    width: 100%;
    max-width: 1440px;
    height: 800px;
    background-color: yellowgreen;
    margin: 0 auto;
    display: flex;
`
const Aboutleft = styled.div`
    width: 50%;
    height: 100%;
    background-color: pink;
    display: flex;
    flex-direction: column;
    justify-content: space-evenly;
`
const Aboutright = styled.div`
    width: 50%;
    height: 100%;
    background-color: blueviolet;
`

const Rightbox = styled.div`
    width: 100%;
    height: 70px;
    background-color: white;
`
function Home() {
    
    return (
        <Container>
            <Head />
            <Aboutme>
                <TitleTob><H3>About Me &gt;</H3></TitleTob>
                <Aboutmecontent>
                    <Aboutleft>
                        <H4>이름 : 전준희</H4>
                        <H4>생년월일 : 99.03.28</H4>
                        <H4>주소 : 대전광역시 동구</H4>
                        <H4>연락처 : 010-2387-7417</H4>
                        <H4>이메일 : wwg0328@naver.com</H4>
                    </Aboutleft>
                    <Aboutright>
                        <Rightbox></Rightbox>
                        <Rightbox></Rightbox>
                        <Rightbox></Rightbox>
                        <Rightbox></Rightbox>
                        <Rightbox></Rightbox>
                        <Rightbox></Rightbox>
                        <Rightbox></Rightbox>
                    </Aboutright>
                </Aboutmecontent>
                <TitleBottom><H3> &lt; Skill</H3></TitleBottom>
            </Aboutme>
        </Container>
    );
}

export default Home;
