import styled from "styled-components";
import styles from "./Body.module.css";
import { useContext, useEffect, useState } from "react";
import { PrContent } from "../contexts/prContent";

function Body2({ content, content2 }) {
  const ImgWrapper = styled.div`
    width: 100vw;
    height: 100vh;
    object-fit: cover;
    color: white;
  `;
  const [book, setBook] = useState(false)
  const onClick = () =>{
    setBook(prevBook => !prevBook)
  }

  return (
    <>
      {content2 && (
        <div className={styles.card}>
        <div className={styles.card_front}>국민카드</div>
        <div className={styles.card_back}>뒷면!!</div>
      </div>
      )}
      {content && (
        <>
          <ImgWrapper>
            <p>안녕하세요</p>
          </ImgWrapper>
          <div className={styles.pos1}>
            <h1>신입 프론트개발자를 희망하는 전준희 입니다. 잘 부탁드립니다.</h1>
          </div>
        </>
      )}

      
    </>
  );
}

export default Body2;
