import styled from "styled-components";
import styles from "./Body.module.css";
import { useContext, useEffect } from "react";
import { PrContent } from "../contexts/prContent";

function Body2({ content, content2 }) {
  const ImgWrapper = styled.div`
    width: 100vw;
    height: 100vh;
    object-fit: cover;
    color: white;
  `;

const [top, setTop] = useState(0);
  const [left, setLeft] = useState(0);

  useEffect(() => {
    // 초기 위치 설정
    setTop(Math.random() * window.innerHeight);
    setLeft(Math.random() * window.innerWidth);

    // 눈송이 이동
    const interval = setInterval(() => {
      setTop(top => top + Math.random() * 5); // top을 랜덤한 값으로 증가시켜 아래로 이동
      setLeft(left => left + Math.random() * 2 - 1); // left를 약간씩 좌우로 이동
    }, 100);

    // 컴포넌트 언마운트 시 clearInterval로 interval 정리
    return () => clearInterval(interval);
  }, []);

  return (
    <>
      {content2 && (
         <div className={styles.snowflake} style={{ top: `${top}px`, left: `${left}px` }}>
    </div>
      )}
      {content && (
        <>
          <ImgWrapper>
            <p>안녕하세요</p>
          </ImgWrapper>
          <div className={styles.pos1}>
            <h1>신입 프론트개발자를 희망하는 전준희 입니다. 잘 부탁드립니다.</h1>
          </div>
        </>
      )}

      
    </>
  );
}

export default Body2;
