import { useState } from 'react';
import styled, { css } from 'styled-components';

// 공통 스타일 정의
const H1Style = css`
    font-weight: bold;
    font-size: 40px;
`;

const TextboxwrapStyle = css`
    display: flex;
    justify-content: start;
    align-items: center;
    background-color: #F08080;
`;

// 공통 부분 --------------------------------------------
const Container = styled.div`
    width: 100%;
    height: 100%;
`;

const H1 = styled.h1`
    ${H1Style}
`;

const H2 = styled.h2`
    z-index: 999;
    color:${(props) => props.hover ? "white" :"black"} ;
`;

const H3 = styled.h3`
    padding-left: 12px;
    font-size: 30px;
    font-weight: bold;
`;

const H4 = styled.h4``;

const P = styled.p`
    font-size: 28px;
`;

const Smallp = styled.p`
    font-size: 24px;
    color: white;
`;

// Header 시작 --------------------------------------------
const Header = styled.div`
    width: 100%;
    height: 100vh;
    background-color: #282828;
    display: flex;
    position: relative;
`;

const Tawon = styled.div`
    width: 100%;
    height: 100%;
    background-color: #9c9c9c;
    border-radius: 9999px;
    display: flex;
    align-items: center;
    position: absolute;
    right: 50%;
`;

const Tawonwrap = styled.div`
    width: 600px;
    height: 800px;
    position: absolute;
    left: 1113px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
`;

const Textbox = styled.div`
    width: 50%;
    height: 100%;
    position: absolute;
    right: 0;
    display: flex;
    align-items: center;
    padding: 0 153px 0 12px;
`;

const Textboxwrap = styled.div`
    width: 100%;
    height: 800px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: end;
    position: relative;
    overflow: hidden;
`;

const TextboxwrapDiv = styled.div`
    width: 100%;
    height: 100px;
    background-color: black;
    position: absolute;
    transition: all 0.5s linear;
    left: ${(props) => props.hover ? "0" :"100%"};
`;
const TextboxwrapHome = styled.div`
    ${TextboxwrapStyle}
    width: 100%;
    height: 100px;
`;

const TextboxwrapAbout = styled.div`
    ${TextboxwrapStyle}
    width: 95%;
    height: 100px;
`;

const TextboxwrapEd = styled.div`
    ${TextboxwrapStyle}
    width: 90%;
    height: 100px;
`;

const TextboxwrapPo = styled.div`
    ${TextboxwrapStyle}
    width: 85%;
    height: 100px;
`;

const TextboxwrapFi = styled.div`
    ${TextboxwrapStyle}
    width: 80%;
    height: 100px;
`;



// Header 끝 --------------------------------------------
function Home() {
    const [hover, setHover] = useState(false)
    const onMouseEnter = (e) =>{
        setHover(true)
    }
    const onMouseLeave = (e) =>{
        setHover(false)
    }
    return (
        <Container>
            <Header>
                <Tawon>
                    <Tawonwrap>
                        <H1>
                            프론트엔드 개발자<br></br>&<br />
                            웹퍼블리셔<br />
                            희망하는 전준희입니다.
                        </H1>
                        <P>
                            저는 프론트엔드 개발과 웹퍼블리셔를 공부하고 있는 전준희 입니다.
                            React와 JavaScript를 주로 다루며, 웹 개발을 통해 사용자 경험을
                            개선하는 것을 즐기고 있습니다.
                        </P>
                        <Smallp>
                            좋게봐주시면 감사하겠습니다.!!<br />
                            잘부탁드립니다.
                        </Smallp>
                    </Tawonwrap>
                </Tawon>
                <Textbox>
                    <Textboxwrap>
                        <TextboxwrapHome  onMouseEnter={onMouseEnter} onMouseLeave={onMouseLeave}>
                            <TextboxwrapDiv hover={hover}></TextboxwrapDiv>
                            <H3>Home</H3>
                        </TextboxwrapHome>
                        <TextboxwrapAbout>
                            <H3>About Me</H3>
                        </TextboxwrapAbout>
                        <TextboxwrapEd>
                            <H3>Education</H3>
                        </TextboxwrapEd>
                        <TextboxwrapPo>
                            <H3>Portfolio</H3>
                        </TextboxwrapPo>
                        <TextboxwrapFi>
                            <H3>Figma</H3>
                        </TextboxwrapFi>
                    </Textboxwrap>
                </Textbox>
            </Header>
        </Container>
    );
}

export default Home;
