import styled from "styled-components";
import styles from "./Body.module.css";
import { useContext, useEffect, useState } from "react";
import { PrContent } from "../contexts/prContent";

function Body2({ content, content2 }) {
  const ImgWrapper = styled.div`
    width: 100vw;
    height: 100vh;
    object-fit: cover;
    color: white;
  `;
  const [card, setCard] = useState(false)
  const onClick = () =>{
    setCard(!card)
  }

  return (
    <>
      {content2 && (
        <div>

          <div className={styles.wrap} onClick={onClick}>
          {/* cardFlipped 상태에 따라 CSS 클래스를 조건부로 적용하여 카드를 뒤집는 애니메이션을 적용합니다. */}
          <div className={`${styles.card} ${card ? styles.flipped : ""}`}>
            <div className={styles.card_front}>FRONT</div>
            <div className={styles.card_back}>BACK</div>
          </div>
        </div>
        </div>
      )}
      {content && (
        <>
          <ImgWrapper>
            <p>안녕하세요</p>
          </ImgWrapper>
          <div className={styles.pos1}>
            <h1>신입 프론트개발자를 희망하는 전준희 입니다. 잘 부탁드립니다.</h1>
          </div>
        </>
      )}

      
    </>
  );
}

export default Body2;
