import styled from "styled-components";
import computer from "../assets/컴퓨터2.jpg"
import mouse from "../assets/마우스.png"
import styles from "./Body.module.css"
function Body(){
    const [nav, setNav] = useState(false)
  const handleClick = () =>{
    setNav(true)
  }
  // 신입 개발자 밑에 클릭버튼 만들고 클릭하면 Nav -> Nav2로 바꿔준다.
  return (
    <>
       <div className={`${nav ? `${styles.height1} ${styles.transition}` : `${styles.height} ${styles.transition}`}`}>
        <div className={nav? styles.won : ""} >
          {nav ? <Nav2 /> : <Nav onClick={handleClick}/>}
        </div>
      </div>
    </>
  );
}

export default Body;
