import styled from "styled-components";
import styles from "./Body.module.css"
function Body2({content, onClick}){
    const ImgWraper = styled.div`
    width: 100vw;
    height: 100vh;
    object-fit: cover;
    color: white;
`
const ImgWraper2 = styled.div`
        width: 100%;
        height: 100%;
        object-fit: cover;
        padding-top: 16px;
    `
    return(
        <>
        {content ? (<>
            <ImgWraper> 
                안녕하세요
            </ImgWraper>
            <div className={styles.pos}>
                <h1>신입 프론트개발자를 희망하는 전준희 입니다. 잘 부탁드립니다.</h1>
            </div>
        </>) : (<>
            <div className={styles.body_container}>
            <ImgWraper> 
                <img className={styles.img} src={computer}/>
            </ImgWraper>
            <div onClick={onClick} className={styles.pos}>
                <h1 >신입 프론트개발자를 희망하는 전준희 입니다.
                    <div className={styles.content}>잘 부탁드립니다.</div>
                </h1>
                <div onClick={onClick}>
                    <ImgWraper2>
                        <img  className={styles.mouse} src={mouse} />
                    </ImgWraper2>

                </div>
                {/* 클릭하는 부분 만들어주고 애니메이션 효과도 만들어준다. */}
                
                <div className={styles.click}>클릭해보세요!!</div>
            </div>
            </div>
        </>)}
       
        </>
    )
}

export default Body2;
