import styled from "styled-components";
import styles from "./Body.module.css";
import { useContext, useEffect, useState } from "react";
import { PrContent } from "../contexts/prContent";

function Body2({ content, content2 }) {
  const ImgWrapper = styled.div`
    width: 100vw;
    height: 100vh;
    object-fit: cover;
    color: white;
  `;

const ImgWraper2 = styled.div`
        width: 100%;
        height: 100%;
        object-fit: cover;
        padding-top: 16px;
    `
  const [cardFlipped, setCardFlipped] = useState(false);
  const [card1Clicked, setCard1Clicked] = useState(false);

  const onClick = () => {
    if (!cardFlipped) {
      setCard1Clicked(true); // card1이 클릭되었음을 설정합니다.
      setCardFlipped(true); // 카드를 뒤집은 상태로 설정합니다.
    } else {
      setCardFlipped(false); // 카드를 원래 상태로 되돌립니다.
    }
  };

  return (
    <>
      {content2 && (
        <div className={styles.card_container}>
          <div className={styles.wrap} >
            <div className={`${cardFlipped ? styles.card1 : ""}`}>
             <ImgWraper2>
                <img  className={styles.mouse} src={mouse} />
              </ImgWraper2>
            </div>
            <div onClick={onClick} className={`${styles.card} ${cardFlipped ? styles.flipped : ""}`}>
              <div className={styles.card_front}>FRONT</div>
              <div className={styles.card_back}>BACK</div>
            </div>
          </div>
        </div>
      )}
      {content && (
        <>
          <ImgWrapper>
            <p>안녕하세요</p>
          </ImgWrapper>
          <div className={styles.pos1}>
            <h1>신입 프론트개발자를 희망하는 전준희 입니다. 잘 부탁드립니다.</h1>
          </div>
        </>
      )}
    </>
  );
}

export default Body2;
