import styled from "styled-components";
import computer from "../assets/컴퓨터2.jpg";
import mouse from "../assets/마우스.png";
import styles from "./Body.module.css";
import Body1 from "./Body1";
import Body2 from "./Body2";

function Body({ setNav, nav }) { // setNav와 nav props를 받아옴
  // 클릭 이벤트 핸들러
  const handleClick = () => {
    setNav(true); 
  };

  return (
    <>
      <div className={`${nav ? `${styles.height1} ${styles.transition}` : `${styles.transition}`}`}>
        <div className={nav ? styles.won : ""}>
          {/* Body1 또는 Body2 컴포넌트 렌더링 */}
          {nav ? <Body2 /> : <Body1 onClick={handleClick} />}
        </div>
      </div>
    </>
  );
}

export default Body;
