import Nav from './Nav';
import Body from './Body';
import { useState } from 'react';
import { Outlet } from 'react-router-dom';

function App() {
  const [nav, setNav] = useState(false);

  const handleClick = () => {
    setNav(true);
  };
console.log(nav);
  return (
    <>
      <Nav />
      {/* Body 컴포넌트에 setNav 함수와 nav 상태를 props로 전달 */}
      {/* <Body setNav={setNav} nav={nav} onClick={handleClick}/> */}
      {nav ? <Outlet /> : <Body nav={nav} onClick={handleClick}/>}
    </>
  );
}

export default App;
