import styles from "./Portfoliohover.module.css"
import buddiz from "../assets/버디.jpg"
import FTT from "../assets/FTT.jpg"
import site from "../assets/사이트.jpg"
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import styles1 from './YourComponent.module.css';
function Portfoliohover({setBuddizClick,buddizClick}){
    console.log(buddizClick);
    console.log("setBuddiz",setBuddizClick);
    const del = () =>{
        setBuddizClick(false)
    }

    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1
    };
    return(
        <>
        {
            buddizClick === "0" && (<>
                 <div className={styles1.sliderContainer}>
            <Slider {...settings}>
                <div>
                    <img src={image1} alt="Image 1" />
                </div>
                <div>
                    <img src={image2} alt="Image 2" />
                </div>
                {/* Add more <div> elements for additional images */}
            </Slider>
        </div>
            <button className={styles.button} onClick={del}>X</button>
            </>)
        }
        {
            buddizClick === "1" && (<>
                <div className={styles.container}>
            <div className={styles.left}>
                <img src={FTT} alt=""/>
            </div>
            <div className={styles.right}>
                <h1>FTT</h1>
                <p>fdgfdgfdgfd</p>
            </div>
        </div>
            <button className={styles.button} onClick={del}>X</button>
            </>)
        }
        {
            buddizClick === "2" && (<>
                <div className={styles.container}>
            <div className={styles.left}>
                <img src={site} alt=""/>
            </div>
            <div className={styles.right}>
                <h1>Portfolio</h1>
                <p>fdgfdgfdgfd</p>
            </div>
        </div>
            <button className={styles.button} onClick={del}>X</button>
            </>)
        }
        
        </>
    )
}

export default Portfoliohover;
