import React, { useState } from "react";
import styels from "./Features.module.css";
import img1 from "../../assets/학원.jpg";
import img2 from "../../assets/학원2.jpg";
import img3 from "../../assets/학원3.jpg";
import Img1 from "./Img1";
import Img2 from "./Img2";
import Img3 from "./Img3";

function Features() {
  const [hoveredImage, setHoveredImage] = useState(null);

  const handleHover = (image) => {
    console.log(image);
    setHoveredImage(image);
  };

  const handleHoverOut = () => {
    setHoveredImage(null);
  };

  return (
    <div className={styels.container}>
      <div className={styels.left}>
        <div className={styels.name}>DW아카데미 학원</div>
        <div className={styels.left_wrap}>
          <div className={styels.left_wrap_left}>
            <div
              onMouseEnter={() => handleHover(img1)}
              onMouseLeave={handleHoverOut}
              className={styels.img_wrap}
            >
              <img src={img1} alt="수료증1" />
            </div>
            <div
              onMouseEnter={() => handleHover(img2)}
              onMouseLeave={handleHoverOut}
              className={styels.img_wrap}
            >
              <img src={img2} alt="수료증2" />
            </div>
            <div
              onMouseEnter={() => handleHover(img3)}
              onMouseLeave={handleHoverOut}
              className={styels.img_wrap}
            >
              <img src={img3} alt="수료증3" />
            </div>
          </div>
          <div className={styels.left_wrap_right}>
            <div className={styels.hover}>
              {hoveredImage === img1 ? <Img1 /> : hoveredImage === img2 ? <Img2 /> : hoveredImage === img3 ? <Img3 /> : <Img1 />}
            </div>
          </div>
        </div>
      </div>

      <div className={styels.right}>
        <div>배운 기술</div>
      </div>
    </div>
  );
}

export default Features;
